// Parse the AI response for action triggers
const aiResponse = $json.text || $json.output || '';

// Check for PDF generation trigger
if (aiResponse.includes('GENERATE_PDF:')) {
  const match = aiResponse.match(/GENERATE_PDF:([^:]+):(.+)/);
  if (match) {
    return [
      {
        json: {
          action: 'generate_pdf',
          scheme: match[1],
          userData: match[2],
          originalResponse: aiResponse.replace(/GENERATE_PDF:[^\n]+/, '')
        }
      }
    ];
  }
}

// Check for agent connection trigger
if (aiResponse.includes('CONNECT_AGENT:')) {
  const match = aiResponse.match(/CONNECT_AGENT:([^:]+):([^:]+):(.+)/);
  if (match) {
    return [
      {
        json: {
          action: 'connect_agent',
          scheme: match[1],
          phone: match[2],
          location: match[3],
          originalResponse: aiResponse.replace(/CONNECT_AGENT:[^\n]+/, '')
        }
      }
    ];
  }
}

// No action needed, just regular response
return [{
  json: {
    action: 'regular_response',
    response: aiResponse
  }
}];

// Generate PDF application form
const scheme = $json.scheme;
const userData = $json.userData || '{}';

// Parse user data if it's a string
let parsedUserData = {};
try {
  parsedUserData = typeof userData === 'string' ? JSON.parse(userData) : userData;
} catch (e) {
  // If parsing fails, extract from the userData string manually
  const nameMatch = userData.match(/name[:\s]+([^,\n]+)/i);
  const mobileMatch = userData.match(/mobile[:\s]+([^,\n]+)/i);
  const businessMatch = userData.match(/business[:\s]+([^,\n]+)/i);
  const employeesMatch = userData.match(/employees[:\s]+([^,\n]+)/i);
  const locationMatch = userData.match(/location[:\s]+([^,\n]+)/i);
  const turnoverMatch = userData.match(/turnover[:\s]+([^,\n]+)/i);

  parsedUserData = {
    name: nameMatch ? nameMatch[1].trim() : '',
    mobile: mobileMatch ? mobileMatch[1].trim() : '',
    businessType: businessMatch ? businessMatch[1].trim() : '',
    employees: employeesMatch ? employeesMatch[1].trim() : '',
    location: locationMatch ? locationMatch[1].trim() : '',
    turnover: turnoverMatch ? turnoverMatch[1].trim() : ''
  };
}

// Create PDF content based on scheme
let pdfContent = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <style>
        body { 
            font-family: Arial, sans-serif; 
            margin: 20px; 
            line-height: 1.6;
        }
        .header { 
            text-align: center; 
            margin-bottom: 30px; 
            border-bottom: 2px solid #333;
            padding-bottom: 20px;
        }
        .form-group { 
            margin: 15px 0; 
            display: flex;
            align-items: center;
        }
        .label { 
            font-weight: bold; 
            min-width: 200px;
            display: inline-block;
        }
        .value {
            border-bottom: 1px solid #666;
            min-width: 250px;
            padding: 5px;
        }
        .signature { 
            margin-top: 50px; 
            display: flex;
            justify-content: space-between;
        }
        .signature-box {
            border-top: 1px solid #000; 
            width: 200px;
            text-align: center;
            padding-top: 10px;
        }
        .documents {
            margin-top: 30px;
            padding: 15px;
            background-color: #f5f5f5;
            border-left: 4px solid #007bff;
        }
        .documents ul {
            margin: 10px 0;
        }
        .documents li {
            margin: 5px 0;
        }
    </style>
</head>
<body>
    <div class="header">
        <h2>Application Form - ${scheme}</h2>
        <p>Government of India Insurance Scheme</p>
        <p style="font-size: 12px; color: #666;">Please fill in any missing information before submission</p>
    </div>

    <div class="form-group">
        <span class="label">Applicant Name:</span>
        <span class="value">${parsedUserData.name || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Mobile Number:</span>
        <span class="value">${parsedUserData.mobile || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Business Type:</span>
        <span class="value">${parsedUserData.businessType || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Number of Employees:</span>
        <span class="value">${parsedUserData.employees || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Business Location:</span>
        <span class="value">${parsedUserData.location || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Annual Turnover:</span>
        <span class="value">${parsedUserData.turnover || '______________________________'}</span>
    </div>

    <div class="form-group">
        <span class="label">Scheme Applied For:</span>
        <span class="value">${scheme}</span>
    </div>

    <div class="form-group">
        <span class="label">Application Date:</span>
        <span class="value">${new Date().toLocaleDateString('en-IN')}</span>
    </div>

    <div class="documents">
        <h3>üìã Required Documents Checklist:</h3>
        <ul>
            <li>‚òê PAN Card Copy (Self-attested)</li>
            <li>‚òê Aadhar Card Copy (Self-attested)</li>
            <li>‚òê Business Registration Certificate</li>
            <li>‚òê Bank Account Statement (Last 6 months)</li>
            <li>‚òê GSTIN Certificate (if applicable)</li>
            <li>‚òê Employee List with Aadhar (if applicable)</li>
            <li>‚òê Recent Passport Size Photographs (2 copies)</li>
            <li>‚òê Business Address Proof</li>
        </ul>

        <h4>Submission Process:</h4>
        <ol>
            <li>Fill in any missing information in this form</li>
            <li>Attach all required documents</li>
            <li>Submit to nearest ${scheme} office or authorized center</li>
            <li>Keep acknowledgment receipt for tracking</li>
        </ol>
    </div>

    <div class="signature">
        <div class="signature-box">
            <p>Date: ${new Date().toLocaleDateString('en-IN')}</p>
            <p>Applicant Signature</p>
        </div>
        <div class="signature-box">
            <p>Received by:</p>
            <p>Officer Signature & Stamp</p>
        </div>
    </div>

    <div style="margin-top: 30px; font-size: 12px; color: #666; text-align: center;">
        <p>This form was generated by FinBuddy AI Assistant | For support: contact your nearest insurance office</p>
    </div>
</body>
</html>
`;

return [{
  json: {
    pdfContent: pdfContent,
    filename: `${scheme.replace(/\s+/g, '_')}_application_${Date.now()}.html`,
    response: $json.originalResponse + "\n\nüìÑ **Your PDF application form has been generated!**\n\n‚úÖ The form is pre-filled with your details\nüìã Includes complete document checklist\nüìù Ready for download and submission\n\nPlease review, print, and submit with required documents to the nearest office.",
    scheme: scheme,
    userData: parsedUserData
  }
}];

// Convert PDF content to downloadable response
const pdfContent = $json.pdfContent;
const filename = $json.filename;
const response = $json.response;

// Create a data URL for the HTML content that can be downloaded
const dataUrl = `data:text/html;charset=utf-8,${encodeURIComponent(pdfContent)}`;

const downloadMarkdown = `üîó **Download Link:** [Open Application Form](${dataUrl})\n\nüí° *Right-click and Save As, or open in browser and Print to PDF.*`;

return [{
  json: {
    text: `${response}\n\n${downloadMarkdown}`,
    filename: filename,
    content: pdfContent,
    contentType: 'text/html'
  }
}];

// Prepare response after connecting to agent
const scheme = $json.scheme;
const phone = $json.phone;
const location = $json.location;

return [{
  json: {
    text: $json.originalResponse + `

‚úÖ **Great! I've connected you with a verified ${scheme} specialist.**

üì± **What happens next:**
‚Ä¢ Agent will contact you on ${phone} within 24 hours
‚Ä¢ They'll provide personalized quotes for your business in ${location}
‚Ä¢ You'll get expert guidance on coverage options
‚Ä¢ Fast-track application processing

üéØ **Agent Details:**
üë§ Name: Rajesh Kumar
üè¢ Specialization: ${scheme}
‚≠ê Rating: 4.8/5 (200+ happy customers)
üìû Direct Contact: Available on WhatsApp

üí¨ **For immediate assistance, you can also message the agent directly on WhatsApp.**`
  }
}];

// Handle regular responses (no special actions)
const response = $json.response || $json.text || '';

return [{
  json: {
    text: response
  }
}];
